function validateForm() {
  var arg = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : {};
  _dialog.elm.submit.disabled = arg.forceDisabled || !_dialog.elm.form.checkValidity();
}

function submitFn(e) {
  e.preventDefault();
  validateForm({
    forceDisabled: true
  });
  postData(_dialog).then(function (d) {
    validateForm();

    _dialog.close();

    getData();
  });
}

